using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Globalization;

namespace UmaPay.Shared
{
    /// <summary>
    /// Provides methods for generating payment notification email templates
    /// </summary>
    public static class PaymentNotificationTemplate
    {
        private static readonly CultureInfo ColombianCulture = new CultureInfo("es-CO");

        public const string Subject = "Comprobante de pago Grupo UMA";

        // Logo de la compañía en base64
        private const string CompanyLogoBase64 = @"";


        public static string GetTemplate(string reference,string customerName,IEnumerable<(string Number, decimal Amount)> invoices,decimal totalAmount,DateTime paymentDate)
        {
            var sb = new StringBuilder();

            // Start main container
            sb.AppendLine("<div style='font-family: Segoe UI, Arial, sans-serif; max-width: 800px; margin: 0 auto; color: #333;'>");

            // Header with logo section
            sb.AppendLine("<div style='display: flex; justify-content: space-between; align-items: center; margin-bottom: 30px;'>");
            sb.AppendLine("<div style='flex: 1;'>");
            sb.AppendLine($"<h2 style='margin: 0; color: #333; font-weight: normal;'>Hola {customerName}!</h2>");
            sb.AppendLine("<p style='margin: 5px 0;'>Gracias por tu pago</p>");
            sb.AppendLine("</div>");



            // Logos section
            sb.AppendLine("<div style='text-align: right;'>");
            //sb.AppendLine($"<img src='data:image/jpeg;base64,{CompanyLogoBase64}' alt='Grupo UMA Logo' style='max-width: 300px; height: auto;'/>");
            sb.AppendLine($@"<img src='data:image/jpeg;base64,{CompanyLogoBase64}' alt='Logo' style='max-width: 300px;'/>");

            //sb.AppendLine("<img src='https://www.portal80.com.co/web2014/wp-content/uploads/2021/11/logo_uma_bajaj.jpg' alt='Logo'/>");


            sb.AppendLine("</div>");
            sb.AppendLine("</div>");

            // Payment details section
            sb.AppendLine("<div style='background-color: #f8f9fa; padding: 20px; border-radius: 4px; margin-bottom: 20px;'>");
            sb.AppendLine("<h3 style='margin: 0 0 15px 0; color: #333;'>Detalle de pago</h3>");

            // Payment information
            sb.AppendLine("<table style='width: 100%; border-collapse: collapse;'>");
            sb.AppendLine($"<tr><td style='padding: 5px 0;'>Comprobante de pagos CUS:</td><td>{reference}</td></tr>");
            sb.AppendLine($"<tr><td style='padding: 5px 0;'>Nombre del cliente:</td><td>{customerName}</td></tr>");
            sb.AppendLine($"<tr><td style='padding: 5px 0;'>Fecha de pago:</td><td>{paymentDate:dd/MM/yyyy}</td></tr>");

            // Invoices section
            sb.AppendLine("<tr><td style='padding: 15px 0 5px 0;' colspan='2'><strong>Facturas Pagadas:</strong></td></tr>");
            foreach (var invoice in invoices)
            {
                sb.AppendLine($"<tr><td style='padding: 2px 0;'>{invoice.Number}</td><td>$ {invoice.Amount:N0}</td></tr>");
            }

            // Total amount
            sb.AppendLine($"<tr><td style='padding: 15px 0 5px 0;'><strong>Monto pagado:</strong></td><td><strong>$ {totalAmount:N0}</strong></td></tr>");
            sb.AppendLine("</table>");
            sb.AppendLine("</div>");

            // Disclaimer
            sb.AppendLine("<div style='font-size: 11px; color: #666; border-top: 1px solid #eee; padding-top: 20px;'>");
            sb.AppendLine("El contenido de este correo es confidencial y su uso es legalmente restringido al remitente y destinatario(s) nombrado(s) exclusivamente. Si usted no es el destinatario como tal, no debe leer, copiar, revelar, reenviar o utilizar el contenido de este mensaje.<br/><br/>");
            sb.AppendLine("The content of this e-mail is confidential and its use is legally restricted. the misuse is subject to legal action. If you are not meant as a receiver in any way, you must not read, copy, disclose, forward, or otherwise use the information contained in this email.");
            sb.AppendLine("</div>");

            sb.AppendLine("</div>");

            return sb.ToString();
        }

        /// <summary>
        /// Generates HTML email template for payment notifications
        /// </summary>
        /// <param name="reference">Payment reference number</param>
        /// <param name="customerName">Name of the customer</param>
        /// <param name="invoices">Collection of invoices with their amounts</param>
        /// <param name="totalAmount">Total payment amount</param>
        /// <param name="paymentDate">Date when the payment was processed</param>
        /// <returns>HTML formatted email content</returns>
        public static string GetTemplate2(
            string reference,
            string customerName,
            IEnumerable<(string Number, decimal Amount)> invoices,
            decimal totalAmount,
            DateTime paymentDate)
        {
            var sb = new StringBuilder();
            sb.AppendLine("<div style='font-family: Arial, sans-serif; max-width: 800px; margin: 0 auto;'>");

            // Company Logo
            sb.AppendLine("<div style='text-align: center; margin-bottom: 30px;'>");
            sb.AppendLine($"<img src='data:image/jpeg;base64,{CompanyLogoBase64}' " +
                         "alt='Grupo UMA Logo' style='max-width: 300px; height: auto;'/>");
            sb.AppendLine("</div>");

            // Payment Details Section
            sb.AppendLine("<div style='background-color: #f8f9fa; padding: 20px; border-radius: 5px; margin-bottom: 20px;'>");
            sb.AppendLine("<h2 style='color: #2c3e50; margin-bottom: 20px;'>Detalle de Pago</h2>");

            // Main Information
            sb.AppendLine("<div style='margin-bottom: 15px;'>");
            sb.AppendLine($"<p><strong>Referencia:</strong> {reference}</p>");
            sb.AppendLine($"<p><strong>Nombre de cliente:</strong> {customerName}</p>");

            // Invoices Section
            sb.AppendLine("<p><strong>Facturas, Notas crédito y otros documentos pagados:</strong></p>");
            sb.AppendLine("<ul style='list-style-type: none; padding-left: 20px;'>");
            foreach (var invoice in invoices)
            {
                sb.AppendLine($"<li>#{invoice.Number} por $ {invoice.Amount.ToString("N2", ColombianCulture)}</li>");
            }
            sb.AppendLine("</ul>");

            // Total Amount and Date
            sb.AppendLine($"<p><strong>Monto pagado:</strong> $ {totalAmount.ToString("N2", ColombianCulture)}</p>");
            sb.AppendLine($"<p><strong>Fecha de pago:</strong> {paymentDate.ToString("dd/MM/yyyy", ColombianCulture)}</p>");
            sb.AppendLine("</div>");

            // Contact Information
            sb.AppendLine("<div style='margin-top: 20px;'>");
            sb.AppendLine("<p>Si presenta algún inconveniente puede comunicarse al correo " +
                "<a href='mailto:aplicacionpagos@grupouma.com'>aplicacionpagos@grupouma.com</a> " +
                "o a los celulares 313 8313401 - 3102696408</p>");
            sb.AppendLine("</div>");

            // Signature
            sb.AppendLine("<div style='margin-top: 30px;'>");
            sb.AppendLine("<p>Muchas gracias.</p>");
            sb.AppendLine("<p>Cordialmente,</p>");
            sb.AppendLine("</div>");

            // Footer with Contact Information
            sb.AppendLine(GetContactInformation());

            sb.AppendLine("</div></div>");

            return sb.ToString();
        }

        /// <summary>
        /// Generates the contact information section
        /// </summary>
        private static string GetContactInformation()
        {
            var sb = new StringBuilder();

            sb.AppendLine("<div style='background-color: #f1f1f1; padding: 20px; border-radius: 5px; margin-top: 30px;'>");
            sb.AppendLine("<p style='font-weight: bold;'>NOTA: \"Su satisfacción es primordial para nosotros, " +
                "por lo que le ofrecemos las siguientes vías de contacto para atender sus necesidades oportunamente:\"</p>");

            sb.AppendLine("<ul style='list-style-type: none; padding-left: 0;'>");

            AddContactItem(sb, "reportar pagos o verificar cómo fueron aplicados",
                "aplicacionpagos@grupouma.com");

            AddContactItem(sb, "conciliaciones",
                "conciliaciones@grupouma.com");

            AddContactItem(sb, "estados de cuenta u otros asuntos relacionados con la cartera",
                "gestioncartera@grupouma.com");

            AddContactItem(sb, "pedidos retenidos por el área de liberaciones",
                "luis.restrepo@grupouma.com");

            AddContactItem(sb, "crédito",
                "creditos@grupouma.com");

            AddContactItem(sb, "liquidación de sus promociones",
                "promociones@grupouma.com");

            AddContactItem(sb, "certificados de retenciones e impuestos o enviar sus certificados",
                "impuestos@grupouma.com");

            sb.AppendLine("</ul>");

            sb.AppendLine("<p style='font-style: italic; margin-top: 15px;'>" +
                "\"Agradecemos su preferencia, y trabajamos constantemente para brindarle un servicio de alta calidad.\"</p>");

            sb.AppendLine("</div>");

            return sb.ToString();
        }

        /// <summary>
        /// Helper method to add a contact item to the template
        /// </summary>
        private static void AddContactItem(StringBuilder sb, string description, string email)
        {
            sb.AppendLine("<li style='margin-bottom: 10px;'>");
            sb.AppendLine($"Si requiere <strong>{description}</strong>, ");
            sb.AppendLine("por favor comuníquese al correo ");
            sb.AppendLine($"<a href='mailto:{email}'><strong>{email}</strong></a>");
            sb.AppendLine("</li>");
        }

        /// <summary>
        /// Gets custom styles for the email template
        /// </summary>
        private static string GetCustomStyles()
        {
            return @"
                <style>
                    .payment-details {
                        font-family: Arial, sans-serif;
                        max-width: 800px;
                        margin: 0 auto;
                        padding: 20px;
                    }
                    .section {
                        background-color: #f8f9fa;
                        padding: 20px;
                        border-radius: 5px;
                        margin-bottom: 20px;
                    }
                    .header {
                        color: #2c3e50;
                        margin-bottom: 20px;
                    }
                    .contact-info {
                        background-color: #f1f1f1;
                        padding: 20px;
                        border-radius: 5px;
                        margin-top: 30px;
                    }
                    .contact-list {
                        list-style-type: none;
                        padding-left: 0;
                    }
                    .contact-item {
                        margin-bottom: 10px;
                    }
                </style>";
        }
    }
}
